mapper.select:
select id, name, price, description, teacher_id, cover_photo_url
from course
where id = #{id}

select c.id, c.name, c.price, c.description, c.teacher_id, c.cover_photo_url
from course c

    join category_course cc on c.id = cc.course_id
    where cc.category_id=#{categoryId}


    order by #{orderBy}

select id, name, price, description, teacher_id, cover_photo_url
from course


        and id = #{id}


        and name = #{name}


        and price = #{price}


        and description = #{description}


        and teacher_id = #{teacherId}


        and cover_photo_url = #{coverPhotoUrl}
