mapper.select:
select c.id, c.name, c.price, c.description, c.user_name, c.average_score, c.cover_picture, c.approved, c.create_time, c.update_time, u.id uid, u.username, u.full_name, u.profile_picture from course c join user u on c.user_name = u.username
join category_course cc on c.id = cc.course_id and cc.category_id = #{categoryId}
join user_course uc on c.id = uc.course_id and uc.user_name = #{studentName}
c.user_name = #{teacherName}
c.approved = #{approved}
order by ${orderBy} desc

select id
from user_course
where user_name = #{username}
  and course_id = #{courseId}
